mapper.select:
SELECT COUNT(*) FROM test
teacher_no = ${teacherNo} AND test_title LIKE CONCAT('%',#{searchWord},'%')
teacher_no = ${teacherNo}

SELECT test_no testNo , test_title testTitle , test_date testDate FROM test
teacher_no = ${teacherNo} AND test_title LIKE CONCAT('%',#{searchWord},'%')
teacher_no = ${teacherNo}
ORDER BY test_date DESC LIMIT ${beginRow}, ${rowPerPage}

