mapper.update:

UPDATE
	example
SET
	example_title = #{exampleTitle}, example_ox = #{exampleOx}
WHERE
	example_idx = #{example_idx} AND question_no = #{questionNo}


UPDATE
	question
SET
	question_title = #{questionTitle}
WEHRE
	test_no = #{testNo} AND question_idx = #{questionIdx}


UPDATE
	test
SET
	test_title = #{testTitle}, test_date = #{testDate}
WHERE
	test_no = #{testNo} AND teacher_no = #{teacherNo}

mapper.delete:
DELETE e FROM example e INNER JOIN question q ON e.question_no = q.question_no INNER JOIN test t ON q.test_no = t.test_no WHERE t.test_no = #{testNo};
DELETE FROM question WHERE test_no = #{testNo};
DELETE FROM test WHERE test_no = #{testNo};

mapper.insert:

INSERT INTO example (
	question_no
	, example_idx
	, example_title
	, example_ox
) VALUES (
	#{questionNo}
	, #{exampleIdx}
	, #{exampleTitle}
	, #{exampleOx}
)


INSERT INTO question (
	test_no
	, question_idx
	, question_title
) VALUES (
	#{testNo}
	, #{questionIdx}
	, #{questionTitle}
)


INSERT INTO test (
	teacher_no
	, test_title
	, test_date
) VALUES (
	#{teacherNo}
	, #{testTitle}
	, #{testDate}
)

mapper.select:
SELECT COUNT(*) FROM test
teacher_no = ${teacherNo} AND test_title LIKE CONCAT('%',#{searchWord},'%')
teacher_no = ${teacherNo}

SELECT test_no testNo , test_title testTitle , test_date testDate FROM test
teacher_no = ${teacherNo} AND test_title LIKE CONCAT('%',#{searchWord},'%')
teacher_no = ${teacherNo}
ORDER BY test_date DESC LIMIT ${beginRow}, ${rowPerPage}

mapper.insert:

INSERT INTO teacher (
	teacher_id
	, teacher_pw
	, teacher_name
) VALUES (
	#{teacherId}
	, PASSWORD(#{teacherPw})
	, #{teacherName}
)
